import React from 'react';
import Maths from './Math';
import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			number1:'',
			number2:'',
			jawaban1:'',
			jawaban2:'',
			jawaban3:'',
			jawaban4:''
		}
	}

	componentDidMount(){
		let theReal = Math.floor(Math.random()* Maths.length)
		this.setState({ number1:Maths[theReal].number1})
	}

	render(){
		return(
			<div>
				Number 1 : {this.state.number1}
			</div>
		)
	}

}

export default App;
