import React from 'react';
import List from './List';
import TodoList from './TodoList';
class App extends React.Component{

	constructor(){
		super()
		this.state={
			todo:List
			}
		}

	changeCompleted = (id) => {
		this.setState(prevState => {
			this.state.todo.map(list => {
				if(id===list.id){
					return list.completed = !list.completed	
				}
			})
		})
	}

	render(){

		const todoTask = this.state.todo.map(list => {
			return(
				<TodoList list={list} key={list.id} changeCompleted={this.changeCompleted} />
			)
		})

		return(
		<div>
			{todoTask}
		</div>
		)

		}

}

export default App;
