import React from 'react';

import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			isLoading:true,
			url : '',
			meme : [],
			quote1 : '',
			quote2 : ''
		}
	}

	componentDidMount() {
		fetch('https://api.imgflip.com/get_memes')
		.then(response => response.json())
		.then(meme => {
			const memes = meme.data.memes[Math.floor(Math.random() * meme.data.memes.length)].url
			console.log(meme.data.memes)
			this.setState({url:memes, meme:meme.data.memes , isLoading : false})

		})

	}

	restart = () => {
		const memes = this.state.meme[Math.floor(Math.random() * this.state.meme.length)].url
		this.setState({url:memes})
	}

	render(){
		if(this.state.isLoading){
			return(
				<div id='container'>
					<h1>Loading ! </h1>
				</div>
			)
		}

		else{
			return(
				<div>
					<div id='container'>
						<h1 className='quote1'>{this.state.quote1}</h1>
						<img src={this.state.url} width='400' />
						<h1 className='quote2'>{this.state.quote2}</h1>

					</div>
					<input type='text' name='quote1' onChange={(event)=>{this.setState({quote1:event.target.value})}} />
					<input type='text' name='quote2' onChange={(event)=>{this.setState({quote2:event.target.value})}} />
					<button onClick={this.restart} className='mo'>MOPL</button>
				</div>
			)
			}
		}

}

export default App;
