import React from 'react';


class App extends React.Component{
	constructor(){
		super()
		this.state={
			username:'',
			age:'',
			gender:''
		}
	}

	render(){

		return(
			<div>
				<fieldset>
					<center><h1>Form Practice</h1></center>
					<hr />
					<table>
						<tr>
							<td>UserName</td>
							<td>:</td>
							<td><input type='text' onChange={(event)=> this.setState({username:event.target.value})} /></td>
						</tr>
					</table>
				</fieldset>
				<br />
				<table width='300'>
					<th colspan='3'>Result</th>
					<tr>
						<td>UserName</td>
						<td>:</td>
						<td>{this.state.username}</td>
					</tr>
				</table>

			</div>
		)

	}

}

export default App;
