import React from 'react';
import Restart from './Restart';
import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			number1:'',
			number2:'',
			jawaban1:'',
			jawaban2:'',
			jawaban3:'',
			jawaban4:'',
			isError:false,
			count : 0,
			started:false
		}
	}



	start = () => {
		this.setState({started:true})
		Restart;
	}

	render(){
		if(!this.state.isError) {
			if(this.state.started){
				return(
					<div>
						<h2>Number 1 : {this.state.number1}</h2>
						<h2>Number 2 : {this.state.number2}</h2>
						<h2>Jawaban 1 : {this.state.jawaban1}</h2>
						<h2>Jawaban 2 : {this.state.jawaban2}</h2>
						<h2>Jawaban 3 : {this.state.jawaban3}</h2>
						<h2>Jawaban 4 : {this.state.jawaban4}</h2>
						<button onClick={this.Restart}>Restart</button>
					</div>
				)
			}else{
				return(
					<div>
						<h1> WELCOME TO THE TOP MATH GAME</h1>
						<button onClick={this.start}>start</button>
					</div>
				)
			}
		}else{
			return(
				<div>
					<h1>Error Occurred</h1>
				</div>
			)
		}
	}

}

export default App;
