import React from 'react';

class App extends React.Component{

	constructor(){
		super()
		this.state={
			count:0
			}
		}

	clicked = () => {
		this.setState({count:this.state.count++})
	}

	render(){
		return(
		<div>
			<h2>{this.state.count}</h2>
			<button onClick={this.clicked()}>Click Me!</button>
		</div>
		)

		}

}

export default App;
