import React from 'react';
import Meme from './Meme';
import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			meme : Meme 
		}
	}
	restart() {
		this.setState({meme:Meme})
		}

	render(){

		return(
		<div>
			<div id='container'>
				<h1 className='quote1'>{this.state.meme[Math.floor(5*Math.random())].quote}</h1>
				<img src={this.state.meme[Math.floor(5*Math.random())].image} width='400' height='300' />
				<h1 className='quote2'>{this.state.meme[Math.floor(5*Math.random())].quote}</h1>
				<button onClick={this.restart} className='mo'>MOPL</button>
			</div>
		</div>
		)

	}

}

export default App;
