import React from 'react';

const TodoList = (props) => {

const color = {color:'black'};
	if(props.list.completed){
		color.color = 'red';
	}else{
		color.color = 'blue';
	}

	return(
		<div>
			<p style={color}><input type='checkbox' checked={props.list.completed} onChange={() => props.changeCompleted(props.list.id)} /> {props.list.task} </p>
		</div>

	)

}

export default TodoList;
