import React from 'react';


class App extends React.Component{

	constructor(){
		super()
		this.state = {
			firstName:'anak',
			lastName:'ana'
		}
	}




	render(){
		return(
			<div>
				Firstname <input type='text' name='firstName' /><br/>
				Lastname <input type='text' name='lastName' /><br/><br/>
				{this.state.firstName} {this.state.lastName}
			</div>
		)
	}

}

export default App;
