import React from 'react';

import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			url : '',
			quote1 : '',
			quote2 : ''
		}
	}

	componentDidMount() {
		fetch('https://api.imgflip.com/get_memes')
		.then(response => response.json())
		.then(meme => {

			const memes = meme.data.memes[Math.floor(Math.random() * meme.data.memes.length)]
			console.log(memes)
			this.setState({url:memes})
		})

	}

	restart = () => {

		}

	render(){

		return(
		<div>
			<div id='container'>
				<h1 className='quote1'>{this.state.quote1}</h1>
				<img src={this.state.url} />
				<h1 className='quote2'>{this.state.quote2}</h1>

			</div>
			<input type='text' name='quote1' onChange={(event)=>{this.setState({quote1:event.target.value})}} />
			<input type='text' name='quote2' onChange={(event)=>{this.setState({quote2:event.target.value})}} />
			<button onClick={this.restart} className='mo'>MOPL</button>
		</div>
		)

	}

}

export default App;
