import React from 'react';
import Meme from './Meme';
import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			meme : Meme 
		}
	}

	render(){

		return(
		<div>
			<div id='container'>
				<h1 className='quote1'>{this.state.meme[Math.floor(13*Math.random())].quote}</h1>

				<button className='mo'>MOPL</button>
			</div>
		</div>
		)

	}

}

export default App;
