import React from 'react';
import Maths from './Math';
import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			number1:'',
			number2:'',
			jawaban1:'',
			jawaban2:'',
			jawaban3:'',
			jawaban4:''
		}
	}

	componentDidMount(){
		let theReal = Math.floor(Math.random()* Maths.length)
		this.setState({ 
			number1:Maths[theReal].number1,
			number2:Maths[theReal].number2,
			jawaban1:Maths[theReal].hasil,
		})
	}

	render(){
		return(
			<div>
				<h2>Number 1 : {this.state.number1}</h2>
				<h2>Number 2 : {this.state.number2}</h2>
				<h2>Hasil : {this.state.jawaban1}</h2>
			</div>
		)
	}

}

export default App;
