import React from 'react';
import Meme from './Meme';

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			meme : Meme 
		}
	}

	render(){

		const list = this.state.meme.map(list => {
				return(list.quote)
			})

		return({list})

	}

}

export default App;
