import React from 'react';


class App extends React.Component{

	constructor(){
		super()
		this.state = {
			firstName:'',
			lastName:''
		}
		this.changeName = this.changeName.bind(this)
	}

	changeName(event){
		this.setState({[event.target.name]:event.target.value})

	}



	render(){
		return(
			<div>
				Firstname <input type='text' name='firstName' onChange={this.changeName} /><br/>
				Lastname <input type='text' name='lastName' onChange={this.changeName} /><br/><br/>
				{this.state.firstName} {this.state.lastName}
			</div>
		)
	}

}

export default App;
