import React from 'react';


class App extends React.Component{

	constructor(){
		super()
		this.state = {
			firstName:'',
			lastName:''
		}
	}

	changeName(event){
		this.setState({firstName:event.target.value})

	}



	render(){
		return(
			<div>
				Firstname <input type='text' name='firstName' onChange={(event) => this.changeName} /><br/>
				Lastname <input type='text' name='lastName' onChange={(event)=> this.setState({lastName:event.target.value})} /><br/><br/>
				{this.state.firstName} {this.state.lastName}
			</div>
		)
	}

}

export default App;
