import React from 'react';


class App extends React.Component{

	constructor(){
		super()
		this.state = {
			firstName:'',
			lastName:'',
			check:true,
			gender:'',
			number:''
		}
		this.changeName = this.changeName.bind(this)
	}

	changeName(event){
		if(event.target.type==='checkbox'){
			this.setState({[event.target.name]:event.target.checked})
		}
		else{
			this.setState({[event.target.name]:event.target.value})
		}

	}

	render(){
		return(
			<div>
				Firstname <input type='text' name='firstName' onChange={this.changeName} /><br/>
				Lastname <input type='text' name='lastName' onChange={this.changeName} /><br/><br/>

				<input type='checkbox' name='check' checked={this.state.check} onChange={this.changeName} /> First <br />

				<input 
				type='radio' 
				name='gender' 
				checked={this.state.gender === 'male'} 
				value='male' 
				onChange={this.changeName} /> Male <br />

				<input type='radio' name='gender' checked={this.state.gender === 'female'} value='female' onChange={this.changeName} /> Female <br />

				<br />

				<select
				name='number'
				onChange={this.changeName}
				>
					<option value='1'>1</option>
					<option value='2'>2</option>
					<option value='3'>3</option>
					<option value='4'>4</option>
					<option value='5'>5</option>
				</select>

				<br />

				<h1> Your Name Is : {this.state.firstName} {this.state.lastName} </h1> <br />

				<h1>Your Gender Is : {this.state.gender}</h1>

				<h1>Your Lucky Number Is : {this.state.number}</h1>

			</div>
		)
	}
}

export default App;
