import React from 'react';


class App extends React.Component{
	constructor(){
		super()
		this.state={
			username:'',
			age:'',
			gender:'',
			member:'',
			hobby:['hadi','ask']
		}
		this.changes = this.changes.bind(this)
	}

	changes = (event) => {
		//checkbox is too hard skipped for now
		//if(event.target.type === 'checkbox'){
			//let value = event.target.value
			//if(!this.state.hobby.includes(value)){
				//this.state.hobby.push(value)
				//this.setState({hobby:[value]});
			//}
			//else{
				//this.state.hobby.pop(value)
				//this.setState();
			//}
			//console.log(this.state.hobby)
		//}

			this.setState({[event.target.name]: event.target.value})

	}

	render(){

		return(
			<div>
				<fieldset style={{width:'300px',margin:'0 auto'}}>
					<center><h1>Form Practice</h1></center>
					<hr />
					<table>
						<tr>
							<td>UserName</td>
							<td>:</td>
							<td><input type='text' name='username' onChange={this.changes} /></td>
						</tr>
						<tr>
							<td>Age</td>
							<td>:</td>
							<td><input type='number' name='age' onChange={this.changes} /></td>
						</tr>
						<tr>
							<td>Gender</td>
							<td>:</td>
							<td><input type='radio' value='Male' name='gender'  onChange={this.changes} />Male 
								<input type='radio' value='Female' name='gender' onChange={this.changes} />Female 
							</td>
						</tr>
						//<tr>
							//<td>Hobby</td>
							//<td>:</td>
							//<td><input type='checkbox' value='Futsal' name='hobby'  onChange={this.changes} />Futsal
								//<input type='checkbox' value='Volleyball' name='hobby' onChange={this.changes} />VolleyBall
							//</td>
						//</tr>
						<tr>
							<td>Membership</td>
							<td>:</td>
							<td><select name='member' onChange={this.changes}> 
									<option value=''></option>
									<option value='1 Tahun'>1 Tahun</option>
									<option value='2 Tahun'>2 Tahun</option>
									<option value='5 Tahun'>5 Tahun</option>
								</select>
							</td>
						</tr>
					</table>
				</fieldset>
				<br />
				<table>
					<th colspan='3'>Result</th>
					<tr>
						<td>UserName</td>
						<td>:</td>
						<td>{this.state.username}</td>
					</tr>
					<tr>
						<td>Age</td>
						<td>:</td>
						<td>{this.state.age}</td>
					</tr>
					<tr>
						<td>Gender</td>
						<td>:</td>
						<td>{this.state.gender}</td>
					</tr>
					//<tr>
						//<td>Hobby</td>
						//<td>:</td>
						//<td>{this.state.hobby.map(value=> {return value+' '})}</td>
					//</tr>
					<tr>
						<td>MemberShip</td>
						<td>:</td>
						<td>{this.state.member}</td>
					</tr>
				</table>

			</div>
		)

	}

}

export default App;
