import React from 'react';
import Meme from './Meme';
import './App.css'

class App extends React.Component{
	constructor(){
		super()
		this.state = {
			meme : Meme 
		}
	}

	render(){

		const list = this.state.meme.map((list) => {
				return(
				<div id='container'>
					<h1 className='quote1'>{list.quote}</h1>
					<img src={list.image} width='400' height='300' />
					<h1 className='quote2'>{list.quote}</h1>
					<button className='mo'>MOPL</button>
				</div>
				)
			})

		return(
		<div>
			{list}
		</div>
		)

	}

}

export default App;
